import React from "react";
import { Breadcrumb, Layout, message } from "antd";
import axios from "axios";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import SideBar from "../home/_sideBar";
import EmployeeForm from "./_employee_form.jsx";
import EmployeeTable from "./_employee_table.jsx";
import { Input, Button, Space } from "antd";
import Highlighter from "react-highlight-words";
import { SearchOutlined } from "@ant-design/icons";
import moment from "moment";

const { Content } = Layout;

class Employee extends React.Component {
  formRef = React.createRef();
  constructor(props) {
    super(props);
    this.state = { employee: [], findEmp: [], group: [], role: [] };
  }

  //for validation value in formfields
  validateMessages = {
    required: "${label}を入力してください!",
    whitespace: "${label}は空になっている",
    types: {
      email: "${label}のフォーマットが間違っている!",
      number: "${label}のフォーマットが間違っている!",
    },
    number: {
      range: "${label}は${min}から${max}の間で入力してください！",
    },
  };

  //for create and update => go to route
  onFinish = (values) => {
    if (values.id === undefined) {
      fetch("/api/v1/employee/create", {
        method: "post",
        body: JSON.stringify(values),
        headers: { "Content-Type": "application/json" },
      }).then((response) => {
        message.success("従業員が作成されました。");
        this.formRef.current?.resetFields();
        this.componentDidMount();
      });
    } else {
      console.log(values);
      fetch(`api/v1/employee/update/${values.id}`, {
        method: "put",
        body: JSON.stringify(values),
        headers: { "Content-Type": "application/json" },
      }).then((response) => {
        message.success("従業員が更新されました。");
        this.formRef.current?.resetFields();
        this.componentDidMount();
      });
    }
  };

  //initialize => get employee data from db => into employee state
  componentDidMount() {
    this.setState({
      employee: [],
      group: [],
      role: [],
    });
    axios
      .get("api/v1/employee.json")
      .then((response) => {
        response.data.role.forEach((k) => {
          const newG1 = {
            key: k.id,
            id: k.id,
            role_name: k.role_name,
          };
          this.setState((prevState) => ({
            role: [...prevState.role, newG1],
          }));
        });

        response.data.group.forEach((j) => {
          const newG1 = {
            key: j.id,
            id: j.id,
            group_name: j.group_name,
          };
          this.setState((prevState) => ({
            group: [...prevState.group, newG1],
          }));
        });

        //console.log(response.data.employees);
        response.data.employees.forEach((i) => {
          const newE1 = {
            key: i.id,
            id: i.id,
            name: i.name,
            email: i.email,
            age: i.age,
            role: i.role_name,
            group: i.group_name,
            birthday: i.birthday,
            address: i.address,
          };
          this.setState((prevState) => ({
            employee: [...prevState.employee, newE1],
          }));
        });
        //console.log(this.state.employee);
      })

      .catch((err) => message.error("Error: " + err));
  }

  //employee delete function => go to route
  deleteEmp = (id) => {
    const url = `api/v1/employee/${id}`;
    fetch(url, {
      method: "delete",
    })
      .then((data) => {
        if (data.ok) {
          this.setState({
            employee: this.state.employee.filter(
              (employee) => employee.key !== id
            ),
          });
          message.success("従業員が削除されました。");
          return data.json();
        }
        throw new Error("Network error.");
      })
      .catch((err) => message.error("Error: " + err));
  };

  //push update in table => get record data
  onEdit = (record) => {
    //console.log(record.birthday);
    //for setValue in FormFields
    this.formRef.current?.setFieldsValue({
      id: record.id,
      name: record.name,
      email: record.email,
      age: record.age,
      address: record.address,
      role_id: record.role,
      //birthday: record.birthday,
      //birthday: moment(record.birthday, "YYYY MM DD"),
      group_id: record.group_id,
    });
  };

  getColumnSearchProps = (dataIndex, indexname) => ({
    filterDropdown: ({
      setSelectedKeys,
      selectedKeys,
      confirm,
      clearFilters,
    }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={(node) => {
            this.searchInput = node;
          }}
          placeholder={`検索 ${indexname}`}
          value={selectedKeys[0]}
          onChange={(e) =>
            setSelectedKeys(e.target.value ? [e.target.value] : [])
          }
          onPressEnter={() =>
            this.handleSearch(selectedKeys, confirm, dataIndex)
          }
          style={{ marginBottom: 8, display: "block" }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            {I18n.t("employee.filter.table.search")}
          </Button>
          <Button
            onClick={() => this.handleReset(clearFilters)}
            size="small"
            style={{ width: 90 }}
          >
            {I18n.t("employee.filter.table.reset")}
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              confirm({ closeDropdown: false });
              this.setState({
                searchText: selectedKeys[0],
                searchedColumn: dataIndex,
              });
            }}
          >
            {I18n.t("employee.filter.table.filter")}
          </Button>
        </Space>
      </div>
    ),
    filterIcon: (filtered) => (
      <SearchOutlined style={{ color: filtered ? "#1890ff" : undefined }} />
    ),
    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase())
        : "",
    onFilterDropdownVisibleChange: (visible) => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: (text) =>
      this.state.searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{ backgroundColor: "#ffc069", padding: 0 }}
          searchWords={[this.state.searchText]}
          autoEscape
          textToHighlight={text ? text.toString() : ""}
        />
      ) : (
        text
      ),
  });

  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  handleReset = (clearFilters) => {
    clearFilters();
    this.setState({ searchText: "" });
  };

  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SideBar />
        <Layout className="site-layout">
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>{I18n.t("home.home")}</Breadcrumb.Item>
              <Breadcrumb.Item>{I18n.t("home.menu1.side2")}</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >
              {/* call _employee_form */}
              <EmployeeForm
                onFinish={this.onFinish}
                formRef={this.formRef}
                group={this.state.group}
                role={this.state.role}
                validateMessages={this.validateMessages}
              />

              {/* call _employee_table */}
              <EmployeeTable
                employee={this.state.employee}
                deleteEmp={this.deleteEmp}
                onEdit={this.onEdit}
                getColumnSearchProps={this.getColumnSearchProps}
              />
            </div>
          </Content>
        </Layout>
      </Layout>
    );
  }
}

