import React, { Component, useState } from "react";
import { Table, Space, Popconfirm, Modal } from 'antd';

class EmployeeTable extends Component {
  
  

    render() {
      
      const columns = [
        {
          title: '名前',
          dataIndex: 'name',
          key: 'name',
          render: text => <a>{text}</a>,
        },
        {
          title: '年齢',
          dataIndex: 'age',
          key: 'age',
        },
        {
          title: 'メール',
          dataIndex: 'email',
          key: 'email',
        },
        {
          title: '役割',
          dataIndex: 'role',
          key: 'role',
        },
        {
          title: '',
          key: 'action',
          render: (text, record) => (
            <Space size="middle">
  
              <button onClick={() =>this.props.onUpdate(record)}>Update</button>
              
              <Popconfirm title="Are you sure delete this employee?"
              onConfirm={() => this.props.deleteEmp(record.id)} okText="Yes" cancelText="No"
              >
              <a href="#" type="danger">
                Delete{" "}
              </a>
              </Popconfirm>
            </Space>
          ),
        },
      ];

      

        return(
          
            <Table columns={columns} dataSource={this.props.employee} />
            
          
        );
    }
}
export default EmployeeTable;
