import React, { useState } from "react";
import I18n from "i18n-js";
import { Form, Input, InputNumber, Button, Select, DatePicker, Modal, Radio } from 'antd';

const EmployeeForm = ({ onFinish, role,formRef,validateMessages,group }) => {

  const layout = {
    labelCol: { span: 5 },
    wrapperCol: { span: 13 },
  };

  const [isModalVisible, setIsModalVisible] = useState(false);
  const onChange = (value) => {
    console.log(value)
    formRef.current?.setFieldsValue({
      group_id: value
    });
  setIsModalVisible(false);
  };
  
  const showModal = () => {
  setIsModalVisible(true);
  };

  return(
    <div>
    <Form autoComplete="off" onFinish={onFinish} 
    ref = {formRef}
    {...layout} name="nest-messages" validateMessages={validateMessages}>
      <Form.Item name='name' label={I18n.t("employee.form.columns.column1")} rules={[{ required: true },
      {whitespace: true}, {max: 10}
      ]} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
        <Input />
      </Form.Item>
      <Form.Item name='email' label={I18n.t("employee.form.columns.column2")} rules={[{ type: 'email' }]} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
        <Input />
      </Form.Item>
      <Form.Item name='age' label={I18n.t("employee.form.columns.column3")} rules={[{ type: 'number', min: 0, max: 99 }]} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
        <InputNumber/>
      </Form.Item>
      <Form.Item name='address' label={I18n.t("employee.form.columns.column5")} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
        <Input.TextArea />
      </Form.Item>
      <Form.Item name='birthday' label={I18n.t("employee.form.columns.column6")} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
        <DatePicker />
      </Form.Item>
      <Form.Item name='role_id' label={I18n.t("employee.form.columns.column4")} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
        <Select placeholder="選択してください">
          {role.map((value) => (
            <Select.Option value={value.key}>{value.role_name}</Select.Option>
          ))}
        </Select>
        </Form.Item>
        <Form.Item onClick={showModal} name='group_id' label={I18n.t("employee.form.columns.column7")} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
        {/* <Select placeholder="選択してください">
          {group.map((value) => (
            <Select.Option value={value.key}>{value.group_name}</Select.Option>
          ))}
        </Select> */}
        {role.map((value) => (
            //<Select.Option value={value.key}></Select.Option>
            <Input value={value.group_name}/>
          ))}
        </Form.Item>
      <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 10 }}>
        <Button type="primary" htmlType="submit" >
        {I18n.t("employee.form.columns.button")}
        </Button>
      </Form.Item>
      
      <Form.Item name='id'  >
      </Form.Item>
    </Form>

    <Modal title="会社一連" visible={isModalVisible}>
<Radio.Group name="radiogroup">
{group.map((value) => (
  
<Radio key={value.id} value = { value.id } onChange={() => onChange(value.id)}　>{value.group_name}</Radio>
))}
</Radio.Group>
</Modal>

    </div>
);


}

export default EmployeeForm;
