import React from "react";
import { Breadcrumb, Layout } from "antd";
import axios from "axios";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import SideBar from "../home/_sideBar";
import EmployeeForm from "./_employee_form.jsx";
import EmployeeTable from "./_employee_table.jsx";

const { Content } = Layout;



class Employee extends React.Component {

  constructor(props) {
    super(props);
    this.state = { employee: [] }
  }

  componentDidMount() {
    axios.get("api/v1/employee.json")
          .then((response) => {
          this.setState({employee: response.data});
          console.log(this.state.employee.indexOf);
          })

    // const data = [
    //   { id: 1, name: 'Xiao Ming', email: 'male', age: 12, role: 'Y' },
    //   { id: 2, name: 'Xiao Wang', email: 'female', age: 12, role: 'Y' },
    //   { id: 3, name: 'Floret', email: 'female', age: 12, role: 'Y' }
    // ];

    // this.setState({
    //   employee : data
    // })

  }

  handleDelete = (postId) => {
    fetch(`/api/v1/employee/${postId}`, { method: 'delete' }).
      then((response) => {
        alert('Post deleted successfully')
        //this.fetchPostsList();
      });
  }
  

  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SideBar />
        <Layout className="site-layout">
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>{I18n.t("home.home")}</Breadcrumb.Item>
              <Breadcrumb.Item>{I18n.t("home.menu1.side2")}</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >
            
            <EmployeeForm />
            <EmployeeTable
            employee = {this.state.employee}
            onDelete={this.handleDelete}
            />

            </div>
          </Content>
        </Layout>
      </Layout>
    );
  }
}

export default Employee;
