import React from "react";
import { Breadcrumb, Layout, message } from "antd";
import axios from "axios";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import SideBar from "../home/_sideBar";
import EmployeeForm from "./_employee_form.jsx";
import EmployeeTable from "./_employee_table.jsx";

const { Content } = Layout;

class Employee extends React.Component {
  formRef = React.createRef();
  constructor(props) {
    super(props);
    this.state = { employee: [], findEmp:[], group: [], role: [] }
  }

  //for validation value in formfields
validateMessages = {
  required: '${label}を入力してください!',
  whitespace: '${label}は空になっている',
  types: {
    email: '${label}のフォーマットが間違っている!',
    number: '${label}のフォーマットが間違っている!',
  },
  number: {
    range: '${label}は${min}から${max}の間で入力してください！',
  },
};
  
  //for create and update => go to route
  onFinish = values => {
    if(values.id === undefined){
      fetch('/api/v1/employee/create', {
        method: 'post',
        body: JSON.stringify(values),
        headers: { 'Content-Type': 'application/json' },
      }).then((response) => {
        message.success('従業員が作成されました。');
        this.formRef.current?.resetFields();
        this.componentDidMount();
      });
    }else{ 
      fetch(`api/v1/employee/update/${values.id}`, {
        method: 'put',
        body: JSON.stringify(values),
        headers: { 'Content-Type': 'application/json' },
      }).then((response) => {
        message.success('従業員が更新されました。');
        this.formRef.current?.resetFields();
        this.componentDidMount();
      });
    }
  };

  //initialize => get employee data from db => into employee state
  componentDidMount() {
    this.setState({
      employee: [],
      group: [], role: []
    })
    axios.get("api/v1/employee.json")
          .then((response) => {
            response.data.role.forEach((k) => {
              const newG1 = {
                key: k.id,
                id: k.id,
                role_name: k.role_name
              };
              this.setState((prevState) => ({
                role: [...prevState.role, newG1],
              }));
            });

            response.data.group.forEach((j) => {
              const newG1 = {
                key: j.id,
                id: j.id,
                group_name: j.group_name
              };
              this.setState((prevState) => ({
                group: [...prevState.group, newG1],
              }));
            });
            
            response.data.employees.forEach((i) => {
              const newE1 = {
                key: i.id,
                id: i.id,
                name: i.name,
                email: i.email,
                age: i.age,
                role: i.role_id,
                group: i.group_id,
                birthday: i.birthday,
                address: i.address
              };
              this.setState((prevState) => ({
                employee: [...prevState.employee, newE1],
              }));
            });
            //console.log(this.state.employee);
          })
          
          .catch((err) => message.error("Error: " + err));
  };

  //employee delete function => go to route
  deleteEmp = (id) => {
    const url = `api/v1/employee/${id}`;
    fetch(url, {
      method: "delete",
    })
      .then((data) => {
        if (data.ok) {
          this.setState({
            employee: this.state.employee.filter(
              (employee) => employee.key !== id
            ),
          });
          message.success('従業員が削除されました。');
          return data.json();
        }
        throw new Error("Network error.");
      })
      .catch((err) => message.error("Error: " + err));
  };

  //push update in table => get record data
  onEdit = (record) => {
    //for setValue in FormFields
    this.formRef.current?.setFieldsValue({
      id: record.id,
      name: record.name,
      email: record.email,
      age: record.age,
      address: record.address
    });
  }
  
  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SideBar />
        <Layout className="site-layout">
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>{I18n.t("home.home")}</Breadcrumb.Item>
              <Breadcrumb.Item>{I18n.t("home.menu1.side2")}</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >
            
            {/* call _employee_form */}
            <EmployeeForm 
            onFinish={this.onFinish} 
            formRef={this.formRef}
            group={this.state.group}
            role={this.state.role}
            validateMessages= {this.validateMessages}
            />
            
            {/* call _employee_table */}
            <EmployeeTable
            employee = {this.state.employee}
            deleteEmp = {this.deleteEmp}
            onEdit={this.onEdit}
            />

            </div>
          </Content>
        </Layout>
      </Layout>
    );
  }
}

export default Employee;
