import React, { Component } from "react";
import I18n from "i18n-js";
import { Form, Input, InputNumber, Button, Select, DatePicker } from 'antd';

const layout = {
  labelCol: { span: 5 },
  wrapperCol: { span: 13 },
};

//for validation value in formfields
const validateMessages = {
  required: '${label}を入力してください!',
  whitespace: '${label}は空になっている',
  types: {
    email: '${label}のフォーマットが間違っている!',
    number: '${label}のフォーマットが間違っている!',
  },
  number: {
    range: '${label}は${min}から${max}の間で入力してください！',
  },
};


  
class EmployeeForm extends Component {

  onHandleSubmit(e) {
    e.preventDefault();
    this.props.formRef.email.value = "";
  }

    render() {
        return(
            <Form autoComplete="off" onFinish={this.props.onFinish} 
            ref = {this.props.formRef}
            {...layout} name="nest-messages" validateMessages={validateMessages}>
              <Form.Item name='name' label={I18n.t("employee.form.columns.column1")} rules={[{ required: true },
              {whitespace: true}, {max: 10}
              ]} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
                <Input />
              </Form.Item>
              <Form.Item name='email' label={I18n.t("employee.form.columns.column2")} rules={[{ type: 'email' }]} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
                <Input />
              </Form.Item>
              <Form.Item name='age' label={I18n.t("employee.form.columns.column3")} rules={[{ type: 'number', min: 0, max: 99 }]} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
                <InputNumber/>
              </Form.Item>
              <Form.Item name='address' label={I18n.t("employee.form.columns.column5")} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
                <Input />
              </Form.Item>
              <Form.Item name='birthday' label={I18n.t("employee.form.columns.column6")} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
                <DatePicker />
              </Form.Item>
              <Form.Item name='role_id' label={I18n.t("employee.form.columns.column4")} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
                <Select placeholder="選択してください">
                  {this.props.role.map((value) => (
                    <Select.Option value={value.key}>{value.role_name}</Select.Option>
                  ))}
                </Select>
                </Form.Item>
                <Form.Item name='group_id' label={I18n.t("employee.form.columns.column7")} hasFeedback style={{ width:"500px", marginLeft:"200px" }} >
                <Select placeholder="選択してください">
                  {this.props.group.map((value) => (
                    <Select.Option value={value.key}>{value.group_name}</Select.Option>
                  ))}
                </Select>
                </Form.Item>
              <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 10 }}>
                <Button type="primary" htmlType="submit" onClick={this.onHandleSubmit}>
                {I18n.t("employee.form.columns.button")}
                </Button>
              </Form.Item>
              <Form.Item name='id'  >
              </Form.Item>
            </Form>
        );
        
    }

}

export default EmployeeForm;
