import React from "react";
import { Breadcrumb, Layout, message } from "antd";
import axios from "axios";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import SideBar from "../home/_sideBar";
import EmployeeForm from "./_employee_form.jsx";
import EmployeeTable from "./_employee_table.jsx";


const { Content } = Layout;

class Employee extends React.Component {
  formRef = React.createRef();
  constructor(props) {
    super(props);
    this.state = { employee: [], findEmp:[] }
  }

  onFinish = values => {
    //console.log('ID: '+values.id)
    //this.deleteEmp(values.id)
    
    fetch('/api/v1/employee/create', {
      method: 'post',
      body: JSON.stringify(values),
      headers: { 'Content-Type': 'application/json' },
    }).then((response) => {
      message.success('item created successfully');
    location.href = '/employees';
    });
  
  };

  componentDidMount() {
    axios.get("api/v1/employee.json")
          .then((response) => {
            response.data.forEach((i) => {
              const newE1 = {
                key: i.id,
                id: i.id,
                name: i.name,
                email: i.email,
                age: i.age,
                role: i.role,
              };
              this.setState((prevState) => ({
                employee: [...prevState.employee, newE1],
              }));
            });
          //this.setState({employee: response.data});
          console.log('loop '+this.state.findEmp.length)
          })
          .catch((err) => message.error("Error: " + err));
  };

  deleteEmp = (id) => {
    const url = `api/v1/employee/${id}`;
    fetch(url, {
      method: "delete",
    })
      .then((data) => {
        if (data.ok) {
          location.href = '/employees';
          //this.componentDidMount()
          return data.json();
        }
        throw new Error("Network error.");
      })
      .catch((err) => message.error("Error: " + err));
  };

  // updateEmp = (id) => {
  //   axios.get(`api/v1/employee/${id}`)
  //         .then((response) => {
            
  //         const newE2 = {
  //           key: response.data.id,
  //           id: response.data.id,
  //           name: response.data.name,
  //           email: response.data.email,
  //           age: response.data.age,
  //           role: response.data.role,
  //         };
  //         this.setState((prevState) => ({
  //           findEmp: [...prevState.findEmp,newE2],
  //         }));

  //         //console.log(this.state.findEmp?.name)
          
  //         })
          
  //         .catch((err) => message.error("Error: " + err));
  // };

  
  onEdit = (record) => {
    //console.log(record)
    this.formRef.current?.setFieldsValue({
      id: record.id,
      name: record.name,
      email: record.email,
      age: record.age,
      role: record.role
    });
  }
  

  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SideBar />
        <Layout className="site-layout">
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>{I18n.t("home.home")}</Breadcrumb.Item>
              <Breadcrumb.Item>{I18n.t("home.menu1.side2")}</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >
            
            <EmployeeForm 
            onFinish={this.onFinish} 
            formRef={this.formRef}
            />
            
            <EmployeeTable
            employee = {this.state.employee}
            deleteEmp = {this.deleteEmp}
            // updateEmp = {this.updateEmp}
            onEdit={this.onEdit}
            />

            </div>
          </Content>
        </Layout>
      </Layout>
    );
  }
}

export default Employee;
