import React from "react";
import { Table, Space, Popconfirm, Button } from "antd";
import I18n from "i18n-js";

const EmployeeTable = ({
  employee,
  deleteEmp,
  onEdit,
  getColumnSearchProps,
  excelExport,
}) => {
  const columns = [
    {
      title: t("employee.form.columns.column1"),
      dataIndex: "name",
      key: "name",
      sorter: (a, b) => a.name.localeCompare(b.name),
      sortDirections: ["descend", "ascend"],
      ...getColumnSearchProps("name", I18n.t("employee.form.columns.column1")),
      render: (text) => <a>{text}</a>,
    },
    {
      title: I18n.t("employee.form.columns.column3"),
      dataIndex: "age",
      defaultSortOrder: "descend",
      sorter: (a, b) => a.age - b.age,
      key: "age",
    },
    {
      title: I18n.t("employee.form.columns.column2"),
      dataIndex: "email",
      key: "email",
      ...getColumnSearchProps("email", I18n.t("employee.form.columns.column2")),
    },
    {
      title: I18n.t("employee.form.columns.column5"),
      dataIndex: "address",
      key: "address",
    },
    {
      title: I18n.t("employee.form.columns.column6"),
      dataIndex: "birthday",
      key: "birthday",
      autoSize: false,
    },
    {
      title: I18n.t("employee.form.columns.column4"),
      dataIndex: "role",
      key: "role_id",
    },
    {
      title: I18n.t("employee.form.columns.column7"),
      dataIndex: "group",
      key: "group_id",
    },
    {
      title: I18n.t("employee.form.columns.action"),
      key: "action",
      render: (record) => (
        <Space size="middle">
          <Button type="primary" onClick={() => onEdit(record)}>
            {I18n.t("employee.filter.table.update")}
          </Button>
          <Popconfirm
            title="この従業員を削除してもよろしいですか?"
            onConfirm={() => deleteEmp(record.id)}
            okText="はい"
            cancelText="いいえ"
          >
            <Button type="danger">
              {I18n.t("employee.filter.table.delete")}
            </Button>
          </Popconfirm>
        </Space>
      ),
    },
  ];
  return (
    <div>
      <Table
        style={{ whiteSpace: "pre" }}
        columns={columns}
        dataSource={employee}
        pagination={{ pageSize: 5 }}
      />
      <Button type="primary" onClick={excelExport}>
        {I18n.t("employee.form.columns.bexcel")}
      </Button>
    </div>
  );
};

export default EmployeeTable;
