import React, { Component, useState } from "react";
import { Table, Space, Popconfirm, Button } from 'antd';
import I18n from "i18n-js";

class EmployeeTable extends Component {
  
    render() {
      
      const columns = [
        {
          title: I18n.t("employee.filter.table.name"),
          dataIndex: 'name',
          key: 'name',
          sorter: (a, b) => a.name.localeCompare(b.name),
          sortDirections: ["descend", "ascend"],
          ...this.props.getColumnSearchProps('name', I18n.t("employee.filter.table.name")),
          render: text => <a>{text}</a>,
        },
        {
          title: '年齢',
          dataIndex: 'age',
          defaultSortOrder: 'descend',
          sorter: (a, b) => a.age - b.age,
          key: 'age',
        },
        {
          title: 'メール',
          dataIndex: 'email',
          key: 'email',
          ...this.props.getColumnSearchProps('email', I18n.t("employee.filter.table.email")),
        },
        {
          title: 'アドレス',
          dataIndex: 'address',
          key: 'address',
          
        },
        {
          title: '誕生日',
          dataIndex: 'birthday',
          key: 'birthday',
          autoSize: false,
        },
        {
          title: '役割',
          dataIndex: 'role',
          key: 'role',
        },
        {
          title: 'グループ',
          dataIndex: 'group',
          key: 'group',
        },
        {
          title: 'アクション',
          key: 'action',
          render: ( record) => (
            <Space size="middle">
              <Button type='primary' onClick={() =>this.props.onEdit(record)}>更新</Button>
              <Popconfirm title="この従業員を削除してもよろしいですか?"
              onConfirm={() => this.props.deleteEmp(record.id)} okText="はい" cancelText="いいえ"
              >
              <Button type='danger'>
                削除
              </Button>
              </Popconfirm>
            </Space>
          ),
        },
      ];


        return(
            <Table columns={columns} dataSource={this.props.employee} 
            pagination={{ pageSize: 5 }}/>
        );
    }
}
export default EmployeeTable;
