import React, { Component, useState } from "react";
import { Table, Space, Popconfirm, Button } from "antd";
import I18n from "i18n-js";

class EmployeeTable extends Component {
  render() {
    const columns = [
      {
        title: I18n.t("employee.form.columns.column1"),
        dataIndex: "name",
        key: "name",
        sorter: (a, b) => a.name.localeCompare(b.name),
        sortDirections: ["descend", "ascend"],
        ...this.props.getColumnSearchProps(
          "name",
          I18n.t("employee.form.columns.column1")
        ),
        render: (text) => <a>{text}</a>,
      },
      {
        title: I18n.t("employee.form.columns.column3"),
        dataIndex: "age",
        defaultSortOrder: "descend",
        sorter: (a, b) => a.age - b.age,
        key: "age",
      },
      {
        title: I18n.t("employee.form.columns.column2"),
        dataIndex: "email",
        key: "email",
        ...this.props.getColumnSearchProps(
          "email",
          I18n.t("employee.form.columns.column2")
        ),
      },
      {
        title: I18n.t("employee.form.columns.column5"),
        dataIndex: "address",
        key: "address",
      },
      {
        title: I18n.t("employee.form.columns.column6"),
        dataIndex: "birthday",
        key: "birthday",
        autoSize: false,
      },
      {
        title: I18n.t("employee.form.columns.column4"),
        dataIndex: "role",
        key: "role_id",
      },
      {
        title: I18n.t("employee.form.columns.column7"),
        dataIndex: "group",
        key: "group_id",
      },
      {
        title: I18n.t("employee.form.columns.action"),
        key: "action",
        render: (record) => (
          <Space size="middle">
            <Button type="primary" onClick={() => this.props.onEdit(record)}>
              {I18n.t("employee.filter.table.update")}
            </Button>
            <Popconfirm
              title="この従業員を削除してもよろしいですか?"
              onConfirm={() => this.props.deleteEmp(record.id)}
              okText="はい"
              cancelText="いいえ"
            >
              <Button type="danger">
                {I18n.t("employee.filter.table.delete")}
              </Button>
            </Popconfirm>
          </Space>
        ),
      },
    ];

    return (
      <Table
        style={{ whiteSpace: "pre" }}
        columns={columns}
        dataSource={this.props.employee}
        pagination={{ pageSize: 5 }}
      />
    );
  }
}
export default EmployeeTable;
