import React, { Component } from "react";

import { Table, Space, Popconfirm } from 'antd';


class EmployeeTable extends Component {

  columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      render: text => <a>{text}</a>,
    },
    {
      title: 'Age',
      dataIndex: 'age',
      key: 'age',
    },
    {
      title: 'Email',
      dataIndex: 'email',
      key: 'email',
    },
    {
      title: 'Role',
      dataIndex: 'role',
      key: 'role',
    },
    {
      title: 'Action',
      key: 'action',
      render: (text, record) => (
        <Space size="middle">
          
          <a>Update</a>
          <Popconfirm title="Are you sure?"
          onConfirm={() => this.deleteBeer(record.id)} okText="Yes" cancelText="No"
          >
          <a>Delete</a>
          </Popconfirm>
        </Space>
      ),
    },
  ];

  
  deleteBeer = (id) => {
    console.log('hello ', id)
  };

    render() {
        return(
            <Table columns={columns} dataSource={this.props.employee} />
        );
    }
}
export default EmployeeTable;
