import React, { useState } from "react";
import I18n, { placeholder } from "i18n-js";
import moment from "moment";
import {
  Form,
  Input,
  InputNumber,
  Button,
  Select,
  DatePicker,
  Modal,
  Radio,
  Table,
} from "antd";

const { Search } = Input;

const EmployeeForm = ({ onFinish, role, formRef, validateMessages, group }) => {
  const layout = {
    labelCol: { span: 5 },
    wrapperCol: { span: 13 },
  };

  const [searchTerm, setSearchTerm] = useState("");
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [searchGp, setValue] = useState(null);

  // 表示するテーブル列を設定する;
  const columns = [
    {
      dataIndex: "group_name",
      key: "id",
      render: (dataIndex, record) => (
        <a onClick={() => onClick(record.id, record.group_name)}>{dataIndex}</a>
      ),
    },
  ];

  const onClick = (value, gname) => {
    console.log(gname);
    formRef.current?.setFieldsValue({
      group_id: value,
      group_name: gname,
    });
    // ModalBoxを閉める
    setIsModalVisible(false);
  };

  // ModalBoxを呼ぶ
  const showModal = (svalue) => {
    console.log(svalue);

    setValue(svalue);

    setSearchTerm(svalue);
    // ModalBoxを表示する
    setIsModalVisible(true);
  };

  // Cancelボタンを押すとModalBoxを閉める
  const handleCancel = () => {
    setIsModalVisible(false);
  };

  // 将来の日を選択できないように
  const disabledDate = (current) => {
    return current > moment().endOf("day");
  };

  return (
    <div>
      <Form
        style={{ width: "60%", marginLeft: "200px" }}
        autoComplete="off"
        onFinish={onFinish}
        ref={formRef}
        {...layout}
        name="nest-messages"
        validateMessages={validateMessages}
      >
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
          <label style={{ color: "blue" }}>
            <h1>社員情報フォーム</h1>
          </label>
        </Form.Item>

        <Form.Item
          name="name"
          label={I18n.t("employee.form.columns.column1")}
          rules={[{ required: true }, { whitespace: true }, { max: 10 }]}
          hasFeedback
        >
          <Input />
        </Form.Item>
        <Form.Item
          name="email"
          label={I18n.t("employee.form.columns.column2")}
          rules={[{ type: "email", required: true }]}
          hasFeedback
        >
          <Input />
        </Form.Item>
        <Form.Item
          name="age"
          label={I18n.t("employee.form.columns.column3")}
          rules={[{ type: "number", min: 0, max: 99, required: true }]}
          hasFeedback
        >
          <InputNumber style={{ width: "100%" }} />
        </Form.Item>
        <Form.Item
          name="address"
          label={I18n.t("employee.form.columns.column5")}
          rules={[{ required: true }, { whitespace: true }]}
        >
          <Input.TextArea style={{ resize: "none" }} />
        </Form.Item>
        <Form.Item
          name="birthday"
          label={I18n.t("employee.form.columns.column6")}
          rules={[{ required: true }]}
        >
          <DatePicker
            disabledDate={disabledDate}
            placeholder="日付を選択してください"
            style={{ width: "100%" }}
          />
        </Form.Item>
        <Form.Item
          name="role_id"
          label={I18n.t("employee.form.columns.column4")}
          rules={[{ required: true }]}
        >
          <Select placeholder="選択してください">
            {role.map((value) => (
              <Select.Option value={value.key}>{value.role_name}</Select.Option>
            ))}
          </Select>
        </Form.Item>
        <Form.Item
          name="group_name"
          label={I18n.t("employee.form.columns.column7")}
          rules={[{ required: true }]}
        >
          <Search
            placeholder="グループを選択してください"
            onSearch={showModal.bind(this)}
            enterButton
          />
        </Form.Item>
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 10 }}>
          <Button type="primary" htmlType="submit">
            {I18n.t("employee.form.columns.button")}
          </Button>
        </Form.Item>

        <Form.Item name="id" style={{ display: "none" }}>
          <Input />
        </Form.Item>
        <Form.Item name="group_id" style={{ display: "none" }}>
          <Input />
        </Form.Item>
      </Form>
      {/* グループのポップアップフォーム */}
      <Modal
        title="グループ"
        visible={isModalVisible}
        // モーダルフォームのフッターにオーケーボタンを設定する。

        footer={[<Button onClick={handleCancel}>キャンセル</Button>]}
      >
        <Input
          placeholder={searchGp}
          //value={searchGp}
          onChange={(event) => {
            setSearchTerm(event.target.value);
          }}
        />
        <Table
          columns={columns}
          dataSource={group.filter((value) => {
            if (searchTerm == "") {
              return value;
            } else if (
              value.group_name
                ?.toLowerCase()
                .includes(searchTerm?.toLowerCase())
            ) {
              return value;
            }
          })}
          pagination={{ pageSize: 4 }}
        />
      </Modal>
    </div>
  );
};

export default EmployeeForm;
